se'Full':                $cont_size_cls = 'full';                break;            case'small':                $cont_size_cls = 'small';                break;            default;                $cont_size_cls = 'full';                break;        }        if (isset($cont_size_cls) && !empty($cont_size_cls)) {            $container_cls = $cont_size_cls;        } else {            $container_cls = 'full';        }        /*        * Display By date        */// $post_date = explode('/', get_the_date($format));//    $post_year = $post_date[$year_position];        $post_date = explode('/', get_the_date($ctl_story_date));        $post_year = (int)$post_date[$year_position];        if ($story_desc_type == 'full') {            $story_cont = apply_filters('the_content', $post->post_content);        } else {            $story_cont = "<p>" . get_the_excerpt() . "</p>";        }        if ('' != $story_cont) {            $post_content = $story_cont;        }        if (preg_match("/\d{4}/", $ctl_story_date, $match)) {            $year = intval($match[0]); //converting the year to integer            if ($year >= 1970) {                $posted_date = date_i18n(__("$date_formats", 'cool-timeline'), strtotime("$ctl_story_date"));                $posted_date_default = date_i18n(__("$format", 'cool-timeline'), strtotime("$ctl_story_date"));            } else {                $posted_date = $this->safe_strtotime($ctl_story_date, "$date_formats");                $posted_date_default = $this->safe_strtotime($ctl_story_date, "$format");            }        }        $selected='';        if($i==1){            $selected='selected';        }        if (function_exists('get_fa')) {            $post_icon = get_fa(true);        }        if(isset($post_icon)){            $icon=$post_icon;        }else{            if(isset($default_icon)&& !empty($default_icon)){                $icon='<i class="fa '.$default_icon.'" aria-hidden="true"></i>';            }else {                $icon = '<i class="fa fa-clock-o" aria-hidden="true"></i>';            }        }        $clt_icon='';        if (isset($attribute['icons']) && $attribute['icons'] == "YES") {            $clt_icon .='<span class="icon-placeholder">'.$icon.'</span> ';        }        if ($posted_date_default != $display_s_date) {            $display_s_date = $posted_date_default;            $dates_li .= ' <li data-date="' . $story_id . '">'.$clt_icon.'<span class="ctl-story-time ' . $selected . '"  data-date="' .$story_id . '" >'.$posted_date.'</span></li>';        }        if ($cont_size_cls == "full") {            $ctl_thumb = wp_get_attachment_image_src(get_post_thumbnail_id(get_the_ID()), 'large');        } else {            $ctl_thumb = wp_get_attachment_image_src(get_post_thumbnail_id(get_the_ID()), 'medium');        }        $ctl_thumb_url = $ctl_thumb['0'];        $ctl_thumb_width = $ctl_thumb['1'];        $ctl_thumb_height = $ctl_thumb['2'];        $s_l_close='';        if ($stories_images_link =="popup") {            $img_f_url = wp_get_attachment_url(get_post_thumbnail_id($post->ID));            $story_img_link = '<a title="' . get_the_title() . '"  href="' . $img_f_url . '" class="ctl_prettyPhoto">';            $s_l_close='</a>';        } else if ($stories_images_link == "single") {            $story_img_link = '<a title="' . get_the_title() . '"  href="' . get_the_permalink() . '" class="single-page-link">';            $s_l_close='</a>';        } else if ($stories_images_link == "disable_links") {            $story_img_link = '';            $s_l_close='';        }        else {            $s_l_close='';            $story_img_link = '<a title="' . get_the_title() . '"  href="' . get_the_permalink() . '" class="">';        }//video format        if ($story_format == "video") {            //$ctl_video=rwmb_meta('ctl_video' ,'type=oembed' )