ormat_html .= '</div>';        } else {            if (isset($ctl_thumb_url) && !empty($ctl_thumb_url)) {                if ($cont_size_cls == "full") {                    $ctl_format_html .= '<div class="full-width">' . $story_img_link . '<img  class="events-object" src="' . $ctl_thumb_url . '">'.$s_l_close.'</div>';                } else {                    $s_img_w = $ctl_thumb_width;                    $s_img_h = $ctl_thumb_height;                    $ctl_format_html .= '<div class="pull-left">' . $story_img_link . '<img width="' . $s_img_w . '" height="' . $s_img_h . '" class="events-object left_small" src="' . $ctl_thumb_url . '">'.$s_l_close.'</div>';                }            }        }        if($posted_date_default !=$same_day_post) {            $same_day_post = $posted_date_default;            $ctl_html .= '<li  data-date="'.$story_id.'">';        }	       $ctl_html .= '<div class="timeline-post '.$post_skin_cls.'"><h2 class="content-title">'.get_the_title().'</h2>';        $ctl_html .= '<div class="ctl_info event-description ' . $cont_size_cls . '">';        $ctl_html .= $ctl_format_html;         $ctl_html .= '<div class="content-details">' . $post_content . '</div>';         $ctl_html .= '</div></div>';        if($posted_date_default !=$same_day_post) {            $ctl_html .='</li>';        }        $post_content = '';    endwhile;    wp_reset_postdata();}$timeline_id=rand(1,10);$timeline_wrp_id="ctl-horizontal-slider-".$timeline_id;$clt_hori_view = '<div id="'.$timeline_wrp_id.'" class="cool-timeline-horizontal  '.$wrp_cls.'" date-slider="ctl-h-slider-'.$timeline_id.'" data-nav="nav-slider-'.$timeline_id.'"><div class="timeline-wrapper '.$wrapper_cls.'" ><div class="clt_carousel_slider">';$clt_hori_view .= '<ul id="nav-slider-'.$timeline_id.'">';$clt_hori_view .=$dates_li;$clt_hori_view .= '</ul></div> <div  class="clt_caru_slider ">';$clt_hori_view .= '<ul  id="ctl-h-slider-'.$timeline_id.'">';$